package com.patternprogramming;

public class Sixteen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int j=1;j<=5;j++) {
			for(int k=5;k>=j;k--) {
				System.out.print(" ");
			}
			for(int i=1;i<=j;i++) {
				System.err.print(j);
			}
			System.out.println();
		}


	}

}
